package ec.edu.insteclrg.webapp.view.bean;

import java.io.Serializable;
import java.util.Base64;

import javax.annotation.PostConstruct;
import javax.servlet.http.HttpServletRequest;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.context.annotation.Scope;
import org.springframework.stereotype.Component;

import ec.edu.insteclrg.common.enums.UserRole;
import ec.edu.insteclrg.domain.User;
import ec.edu.insteclrg.service.candidato.DatosCandidatoService;
import ec.edu.insteclrg.service.empleador.DatosEmpleadorService;
import ec.edu.insteclrg.webapp.handler.AuthenticationHandler;
import lombok.Getter;
import lombok.Setter;

@Getter
@Setter
@Component
@Scope("view")
public class LoginBean implements Serializable {
	private static final long serialVersionUID = 1L;
	private Integer time;
	private String roleName;
	private String fullName;
	private String foto;
	private String menu;
	private User user;

	final static String SINFOTO = "";
	final static String SINCV = "";

	AuthenticationHandler authenticationHandler = new AuthenticationHandler();

	@Autowired
	DatosCandidatoService candidatoService;

	@Autowired
	DatosEmpleadorService empleadorService;

	@Autowired
	HttpServletRequest httpServletRequest;

	@Autowired
	RedirectBean redirectBean;

	@PostConstruct
	public void init() {

		fullName = authenticationHandler.getFullName().split("\\s+")[0];
		user = authenticationHandler.getUser();
		if (authenticationHandler.getFoto() != null)
			foto = "data:image/png;base64," + Base64.getEncoder().encodeToString(authenticationHandler.getFoto());
		else
			foto = "/resources/images/logo_lrg_200.png";

		roleName = redirectBean.getAuthority().getAuthority().split("_")[1];
		if (redirectBean.getAuthority().getAuthority().equals(UserRole.ROLE_CANDIDATO.toString()))
			menu = "menu/mnu-candidato.xhtml";
		else if (redirectBean.getAuthority().getAuthority().equals(UserRole.ROLE_EMPLEADOR.toString()))
			menu = "menu/mnu-empleador.xhtml";
		else if (redirectBean.getAuthority().getAuthority().equals(UserRole.ROLE_ADMIN.toString()))
			menu = "menu/mnu-admin.xhtml";
	}

}
